// client/src/components/Login.js
import React from 'react';
import './Login.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function Login() {
  const handleLogin = () => {
    window.location.href = `${API_URL}/spotify/login`;
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Welcome to Spotify Language Playlist Sorter</h2>
        <p>
          This app helps you organize your Spotify playlists by language. 
          It will analyze your playlist and create separate playlists for each language detected.
        </p>
        <button className="login-button" onClick={handleLogin}>
          Login with Spotify
        </button>
      </div>
    </div>
  );
}

export default Login;
